import React from 'react';
import { moods } from '../../utils/constants';

const MoodSelector = ({ selectedMood, onMoodSelect }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      <h3 className="text-lg font-semibold text-gray-800 mb-4">How are you feeling today?</h3>
      
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
        {moods.map((mood) => (
          <button
            key={mood.id}
            onClick={() => onMoodSelect(mood)}
            className={`p-4 rounded-lg border-2 transition duration-200 hover:scale-105 ${
              selectedMood?.id === mood.id
                ? 'border-indigo-500 bg-indigo-50 shadow-md'
                : 'border-gray-200 hover:border-gray-300'
            }`}
          >
            <div className="text-3xl mb-2">{mood.emoji}</div>
            <div className="text-sm font-medium text-gray-700">{mood.label}</div>
            <div className="text-xs text-gray-500 mt-1">{mood.description}</div>
          </button>
        ))}
      </div>
      
      {selectedMood && (
        <div className="mt-4 p-3 bg-indigo-50 rounded-lg">
          <p className="text-sm text-indigo-800">
            <span className="font-medium">Current mood:</span> {selectedMood.label} {selectedMood.emoji}
          </p>
          <p className="text-xs text-indigo-600 mt-1">
            I'll tailor my responses to help you feel better.
          </p>
        </div>
      )}
    </div>
  );
};

export default MoodSelector;
